import { useState } from 'react'


const History = ({good, neutral, bad, feedback}) => {
  console.log('feedback:', feedback.join(','))
  if (feedback.length === 0) {
    return (
      <div>
        the app is used by pressing the buttons
      </div>
    )
  }

  return (
    <div>
      <h1>statistics</h1>
      good: {good}
      neutral: {neutral}
      bad: {bad}
    </div>
  )
}

const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)
  const [feedback, setFeedback] = useState([])

  const handleGoodClick = () => {
    setFeedback(feedback.concat('G'))
    setGood(good + 1)
  }

  const handleNeutralClick = () => {
    setFeedback(feedback.concat('N'))
    setNeutral(neutral + 1)
  }

  const handleBadClick = () => {
    setFeedback(feedback.concat('B'))
    setBad(bad + 1)
  }

  const Button = ({ handleClick, text }) => (
    <button onClick={handleClick}>
      {text}
    </button>
  )

  return (
    <div>
      <h1>give feedback</h1>
        <Button handleClick={handleGoodClick} text="good" />
        <Button handleClick={handleNeutralClick} text="neutral" />
        <Button handleClick={handleBadClick} text="bad" />
        <History feedback={{good}, {neutral}, {bad}, {feedback}} />
    </div>
  )
}

export default App
