  return (
    <h1>{props.course}</h1>
  )
}

const Content = (props) => {
  return (
    <p>
    <strong>{props.part}</strong>, {props.exercises} tuntia
    </p>
  )
}

const Total = (props) => {
  return (
    <p>
      Yhteens√§ {props.hours} tuntia.
    </p>
  )
}
const App = () => {
  const course = 'Half Stack application development'
  var part = [];
  var exercises = [];
  part[1] = 'Fundamentals of React'
  exercises[1] = 10
  part[2] = 'Using props to pas data'
  exercises[2] = 7
  part[3] = 'State of component'
  exercises[3] = 14
  var i = 0;


  return (
    <>
    <Header course={course} />
