import { useState } from 'react'

const Help = () => {
  return (
    <div>Use buttons to give Unicafe feedback.</div>
  )
}

const StatisticsLine = ({label, result}) => {
  return (
    <div>{label}: {result}</div>
  )
}

const Statistics = ({good, neutral, bad}) => {
  const total = good + neutral + bad
  const score = good - bad
  const positives = ((good / total) * 100) + '%'
  if (total === 0) {
    <Help />
    return
  }

  return (
    <div>
      <h1>Statistics</h1>
      <StatisticsLine label="good" result={good} />
      <StatisticsLine label="neutral" result={neutral} />
      <StatisticsLine label="bad" result={bad} />
      <StatisticsLine label="average" result={average} />
      <StatisticsLine label="positive" result={positives} />
    </div>
  )
}

const Button = ({ handleClick, text }) => (
  <button onClick={handleClick}>
    {text}
  </button>
)


const App = () => {
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const handleGoodClick = () => setGood(good + 1)
  const handleNeutralClick = () => setNeutral(neutral + 1)
  const handleBadClick = () => setBad(bad + 1)

  return (
    <div>
      <h1>Give feedback</h1>
        <Button handleClick={handleGoodClick} text="good" />
        <Button handleClick={handleNeutralClick} text="neutral" />
        <Button handleClick={handleBadClick} text="bad" />
        <Statistics good={good} neutral={neutral} bad={bad} />
    </div>
  )
}

export default App
